u16 GetUTF8IndexInFontTab(s8 *ptr, u8 size)
{
 u16 addr;
 if(size==12)
 {
		for (addr = 0; addr < sizeof(UTF8FontTab12) / sizeof(UTF8FontTab12[0]) - 1; addr++)
		{
			if ((*ptr == UTF8FontTab12[addr].Index[0]) && 
				 (*(ptr + 1) == UTF8FontTab12[addr].Index[1])&& 
			   (*(ptr + 2) == UTF8FontTab12[addr].Index[2]))
			{
				break;
			}
		}
 }
 else if(size==16)
 {
		for (addr = 0; addr < sizeof(UTF8FontTab16) / sizeof(UTF8FontTab16[0]) - 1; addr++)
		{
			if ((*ptr == UTF8FontTab16[addr].Index[0]) && 
				 (*(ptr + 1) == UTF8FontTab16[addr].Index[1])&& 
			   (*(ptr + 2) == UTF8FontTab16[addr].Index[2]))
			{
				break;
			}
		}  
 }
 return addr;
}

